const menuItems=[document.querySelector(".menu-button"),document.querySelector(".hamburger-svg"),document.querySelector(".close-svg")],toggleMobileNav=()=>{document.querySelectorAll("nav li").forEach(e=>e.classList.toggle("nav-shown")),document.querySelectorAll("header div svg").forEach(e=>e.classList.toggle("svg-hidden"));(()=>{let e=0;const t=menuItems[0];e%2==0?t.setAttribute("aria-label","hide menu"):t.setAttribute("aria-label","show menu"),e++})()},activateMobileNavListener=()=>{menuItems.forEach(e=>{e.addEventListener("click",()=>{toggleMobileNav()}),e.addEventListener("keypress",e=>{13===(e.which||e.keyCode)&&toggleMobileNav()})})};export{toggleMobileNav,activateMobileNavListener};